#!/usr/bin/env python3
"""
FoA Orion Secret Keys API
Basic OrionKeys API Usage Examples
For FG Falcon Controller Security Access
"""
import requests
import json

API_URL = "https://orionkeys-fgbwb0habgdrhyh4.canadacentral-01.azurewebsites.net/API/derive"

print("="*60)
print("OrionKeys API - Python Usage Example")
print("="*60)
print()

# Test parameters
seed = "0xAA,0xBB,0xCC"
key = "IPC01"

print(f"Requesting key for seed: {seed}")
print(f"Using key name: {key}")
print()

try:
    # Make request
    print("Sending request...")
    response = requests.get(API_URL, params={'seed': seed, 'key': key}, timeout=10)
    
    print(f"Status: {response.status_code}")
    print()
    
    if response.status_code == 200:
        print("SUCCESS! API responded.")
        print()
        
        # Get the JSON
        data = response.json()
        
        print("Raw JSON Response:")
        print(json.dumps(data, indent=2))
        print()
        
        # Show what keys are available
        print("Available keys in response:")
        for key_name in data.keys():
            print(f"  - {key_name}")
        print()
        
        # Try to extract the response value
        print("Extracting response value...")
        
        # Method 1: Try exact key names
        if 'responseUInt32' in data:
            print(f"✓ Found 'responseUInt32': {data['responseUInt32']}")
            response_value = data['responseUInt32']
        elif 'response' in data:
            print(f"✓ Found 'response': {data['response']}")
            response_value = data['response']
        else:
                response_value = None
        
        if response_value is not None:
            print()
            print("RESULT:")
            print(f"  Response value: {response_value}")
            print(f"  Hex format:     0x{response_value:08X}")
            
            # Convert to 4 bytes for UDS
            byte1 = (response_value >> 24) & 0xFF
            byte2 = (response_value >> 16) & 0xFF
            byte3 = (response_value >> 8) & 0xFF
            byte4 = response_value & 0xFF
            
            print(f"  Bytes:          {byte1:02X} {byte2:02X} {byte3:02X} {byte4:02X}")
            print(f"  UDS command:    27 02 {byte1:02X} {byte2:02X} {byte3:02X} {byte4:02X}")
    else:
        print(f"ERROR: API returned status {response.status_code}")
        print(f"Response: {response.text}")
        
except requests.exceptions.ConnectionError as e:
    print("ERROR: Cannot connect to API")
    
except requests.exceptions.Timeout:
    print("ERROR: Request timed out")
    print("API is taking too long to respond")
    
except Exception as e:
    print(f"ERROR: {type(e).__name__}: {e}")

print()
print("="*60)
print("Test complete")
print("="*60)
